query:
query {
      _aggregation {
        case {
          bmiBy10: bmi {
            histogram(rangeStart: 0, rangeEnd: 100, rangeStep: 10) {
              key
              count
              avg
            }
          }
          bmiBy20: bmi {
            histogram(rangeStart: 0, rangeEnd: 100, rangeStep: 20) {
              key
              count
              avg
            }
          }
          bmiBy50: bmi {
            histogram(rangeStart: 0, rangeEnd: 100, rangeStep: 50) {
              key
              count
              avg
            }
          }
          bmiIn6Buckets: bmi {
            histogram(binCount:6) {
              key
              count
              avg
            }
          }
          bmiIn10Buckets: bmi {
            histogram(binCount:10) {
              key
              count
              avg
            }
          }
      }
    }
  }
